import React, { useState, useEffect } from 'react';

const UploadSlip: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [message, setMessage] = useState<string>('');
  const [previewUrl, setPreviewUrl] = useState<string>('');

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
      setMessage('');
    }
  };

  // Create object URL for image preview if file is image
  useEffect(() => {
    if (file && file.type.startsWith('image/')) {
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
      return () => URL.revokeObjectURL(url); // cleanup on unmount or file change
    } else {
      setPreviewUrl('');
    }
  }, [file]);

  const handleUpload = async () => {
    if (!file) {
      setMessage('Please upload a file.');
      return;
    }

    const formData = new FormData();
    formData.append('slip', file);

    try {
      // Replace with your API endpoint or logic to handle the file upload
      const response = await fetch('/api/upload-slip', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setMessage('File uploaded successfully!');
      } else {
        setMessage('Failed to upload file. Please try again.');
      }
    } catch (error) {
      setMessage('An error occurred while uploading the file.');
    }
  };

  return (
    <div className="flex flex-col items-center mt-4">
      <h2 className="text-lg font-semibold mb-4">Upload Payment Slip</h2>

      <div className="bg-gray-100 rounded-lg p-4 w-full max-w-sm flex flex-col items-center">
        {/* Preview image if image file */}
        {previewUrl && (
          <img
            src={previewUrl}
            alt="Slip Preview"
            className="mb-4 max-w-full max-h-48 object-contain rounded"
          />
        )}

        {/* File name or no file chosen */}
        <p className="mb-2 text-center text-gray-300">
          {file ? file.name : 'No file chosen'}
        </p>

        {/* File input button */}
        <input
          type="file"
          accept=".jpg,.jpeg,.png,.pdf"
          onChange={handleFileChange}
          className="mb-4"
        />
      </div>

      {/* Upload button below the box */}
      <button
        onClick={handleUpload}
        className="mt-4 bg-blue-500 text-white py-2 px-6 rounded shadow hover:bg-blue-600 transition"
      >
        Upload
      </button>

      {message && (
        <p className={`mt-2 ${message.includes('successfully') ? 'text-green-600' : 'text-red-500'}`}>
          {message}
        </p>
      )}
    </div>
  );
};

export default UploadSlip;
