import React, { createContext, useContext, useState } from 'react';

interface AppContextType {
  user: { username: string; balance: number } | null;
  setUser: React.Dispatch<React.SetStateAction<{ username: string; balance: number } | null>>;
  paymentHistory: { date: string; amount: number; category: string }[];
  setPaymentHistory: React.Dispatch<React.SetStateAction<{ date: string; amount: number; category: string }[]>>;
}

const AppContext = createContext<AppContextType | undefined>(undefined);

export const AppProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<{ username: string; balance: number } | null>(null);
  const [paymentHistory, setPaymentHistory] = useState<{ date: string; amount: number; category: string }[]>([]);

  return (
    <AppContext.Provider value={{ user, setUser, paymentHistory, setPaymentHistory }}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => {
  const context = useContext(AppContext);
  if (context === undefined) {
    throw new Error('useAppContext must be used within an AppProvider');
  }
  return context;
};
