import React, { useState, useContext } from 'react';
import { AppContext } from '../contexts/AppContext';

const ActivityPayment: React.FC = () => {
    const { activities, setActivities } = useContext(AppContext);
    const [amount, setAmount] = useState('');
    const [activityType, setActivityType] = useState('');

    const handleAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setAmount(e.target.value);
    };

    const handleActivityTypeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setActivityType(e.target.value);
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (amount && activityType) {
            const newActivity = {
                id: activities.length + 1,
                type: activityType,
                amount: parseFloat(amount),
                date: new Date().toLocaleDateString(),
            };
            setActivities([...activities, newActivity]);
            setAmount('');
            setActivityType('');
        } else {
            alert('Please fill in all fields');
        }
    };

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold mb-4">Record Activity Payment</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block mb-1">Activity Type</label>
                    <select
                        value={activityType}
                        onChange={handleActivityTypeChange}
                        className="border rounded p-2 w-full"
                    >
                        <option value="">Select Activity</option>
                        <option value="Club Activity">Club Activity</option>
                        <option value="Field Trip">Field Trip</option>
                        <option value="Sports Event">Sports Event</option>
                    </select>
                </div>
                <div>
                    <label className="block mb-1">Amount</label>
                    <input
                        type="number"
                        value={amount}
                        onChange={handleAmountChange}
                        className="border rounded p-2 w-full"
                        placeholder="Enter amount"
                    />
                </div>
                <button
                    type="submit"
                    className="bg-blue-500 text-white rounded p-2"
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default ActivityPayment;
