import React from 'react';
import { useAppContext } from '../contexts/AppContext';
import UploadSlip from './UploadSlip';
import { TopUp } from '../types';

const Dashboard: React.FC = () => {
  const { totalBalance, topUpHistory, userName } = useAppContext();

  return (
    <div className="min-h-screen bg-blue-600 flex flex-col items-center justify-center px-4">
      <h1 className="text-white text-4xl font-bold mb-8 text-center select-none">
        Bonjour, {userName || 'User'}!
      </h1>

      <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-md">
        <div className="mb-6 text-center">
          <h2 className="text-xl font-semibold">Total Balance: {totalBalance} THB</h2>
        </div>

        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2">Top-Up History</h3>
          <ul className="list-disc pl-5 max-h-48 overflow-y-auto">
            {topUpHistory.map((topUp: TopUp, index: number) => (
              <li key={index}>
                {topUp.date} - {topUp.amount} THB
              </li>
            ))}
          </ul>
        </div>

        <div>
          <UploadSlip />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
