import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import readExcelFile from 'read-excel-file'; // Adjust the import if needed

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();

  // Helper function to fetch and read Excel file from public folder
  const loadExcelFromPublic = async (url: string) => {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const blob = await response.blob();
    const file = new File([blob], 'check.xlsx', { type: blob.type });
    return readExcelFile(file);
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    try {
const studentsRaw = await loadExcelFromPublic('/check.xlsx');

// Assume first row is header
const headers = studentsRaw[0]; // e.g. ['username', 'password']

// Map remaining rows to objects with keys from headers
const students = studentsRaw.slice(1).map((row: any[]) => {
  const obj: { [key: string]: any } = {};
  headers.forEach((header: string, index: number) => {
    obj[header] = row[index];
  });
  return obj;
});

// Now you can find user by username/password
const user = students.find(
  (student) =>
    student.username === username &&
    student.password === password
);

      if (user) {
        history.push('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      console.error(err);
      setError('Error reading the Excel file');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form onSubmit={handleLogin} className="bg-white p-6 rounded shadow-md">
        <h2 className="text-2xl mb-4">Login</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <div className="mb-4">
          <label className="block mb-2" htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2" htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="border rounded w-full py-2 px-3"
            required
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
