import React, { useContext } from 'react';
import { AppContext } from '../contexts/AppContext';
import UploadSlip from './UploadSlip';

const Dashboard: React.FC = () => {
    const { totalBalance, topUpHistory } = useContext(AppContext);

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold">Dashboard</h1>
            <div className="mt-4">
                <h2 className="text-xl">Total Balance: {totalBalance} THB</h2>
            </div>
            <div className="mt-4">
                <h3 className="text-lg font-semibold">Top-Up History</h3>
                <ul className="list-disc pl-5">
                    {topUpHistory.map((topUp, index) => (
                        <li key={index}>
                            {topUp.date} - {topUp.amount} THB
                        </li>
                    ))}
                </ul>
            </div>
            <div className="mt-4">
                <UploadSlip />
            </div>
        </div>
    );
};

export default Dashboard;
